import blogPosts from './posts/posts-content.js'
import Page from '../components/page';

const BlogImg = ({image}) => {
    return ( <img className='page-img-small' src={image} alt=''></img>)

}

const BlogPost = ({ post }) => {
    return (
        <div className="row">
            <BlogImg  image={post.thumbnail_img}/>

            Oi
        </div>
    )

}

const BlogPosts = () => {
    const rows = blogPosts.map((post, list_index) => {
        return (<BlogPost key={list_index} post={post} />)
    })

    return(<div> {rows}</div>)
}


const Blog = () => {
        return (
            <div className='container-md main-container'>
                <Page 
                title='A lini ;)' 
                short_description="Programando & se encantando!"
                class_image='page-img-small'
                />  
                <BlogPosts /> 
            </div>        
        )

}

export default Blog
